import React, { useEffect, useState, useRef } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { connect } from './redux/blockchain/blockchainActions';
import { fetchData } from './redux/data/dataActions';
import * as s from './styles/globalStyles';
import styled from 'styled-components';
import i1 from './assets/images/1.png';

export const StyledButton = styled.button`
  padding: 10px;
  border-radius: 50px;
  border: none;
  background-color: #ffffff;
  padding: 12px;
  font-weight: bold;
  color: var(--dark-grey);
  width: 150;
  cursor: pointer;
  box-shadow: 0px 6px 0px -2px rgba(250, 250, 250, 0.3);
  -webkit-box-shadow: 0px 6px 0px -2px rgba(250, 250, 250, 0.3);
  -moz-box-shadow: 0px 6px 0px -2px rgba(250, 250, 250, 0.3);
  :active {
    box-shadow: none;
    -webkit-box-shadow: none;
    -moz-box-shadow: none;
  }
`;

export const ResponsiveWrapper = styled.div`
  display: flex;
  flex: 1;
  flex-direction: column;
  justify-content: stretched;
  align-items: stretched;
  width: 100%;
  @media (min-width: 767px) {
    flex-direction: row;
  }
`;

export const StyledImg = styled.img`
  width: 300px;
  height: 300px;
  @media (min-width: 767px) {
    width: 400px;
    height: 400px;
  }
  transition: width 0.25s;
  transition: height 0.25s;
`;

function App() {
  const dispatch = useDispatch();
  const blockchain = useSelector((state) => state.blockchain);
  const data = useSelector((state) => state.data);
  const [orderAmount, setOrderAmount] = useState(1);
  const [feedback, setFeedback] = useState('Will you get the golden crown ?');
  const [claimingNft, setClaimingNft] = useState(false);

  const claimNFTs = (_amount) => {
    if (_amount <= 0) {
      return;
    }
    setFeedback('Grabbing your Meme Stick...');
    setClaimingNft(true);
    blockchain.smartContract.methods
      .mint(_amount)
      .send({
        gasLimit: '285000',
        to: '0x827acb09a2dc20e39c9aad7f7190d9bc53534192',
        from: blockchain.account,
        value: blockchain.web3.utils.toWei(
          (0.01 * _amount).toString(),
          'ether'
        ),
      })
      .once('error', (err) => {
        console.log(err);
        setFeedback('Hmm, something went wrong maybe try again ?');
        setClaimingNft(false);
      })
      .then((receipt) => {
        setFeedback("You're the happy owner of a Meme Stick ! Congrats ðŸŽ‰");
        setClaimingNft(false);
        dispatch(fetchData(blockchain.account));
      });
  };

  const getData = () => {
    if (blockchain.account !== '' && blockchain.smartContract !== null) {
      dispatch(fetchData(blockchain.account));
    }
  };

  useEffect(() => {
    getData();
    console.log(blockchain);
  }, [blockchain.account]);

  return (
    <s.Screen style={{ backgroundColor: 'var(--white)' }}>
      <s.Container flex={1} ai={'center'} style={{ padding: 24 }}>
        <s.TextTitle
          style={{ textAlign: 'center', fontSize: 35, fontWeight: 'bold' }}
        >
          MEME STICKS CLUB
        </s.TextTitle>
        <s.SpacerMedium />
        <ResponsiveWrapper flex={1} style={{ padding: 24 }}>
          <s.Container flex={1} jc={'center'} ai={'center'}>
            <StyledImg alt={'memestick example'} src={i1} />
            <s.SpacerMedium />
            <s.TextTitle
              style={{ textAlign: 'center', fontSize: 30, fontWeight: 'bold' }}
            >
              {data.totalSupply}/2500 minted
            </s.TextTitle>
          </s.Container>
          <s.SpacerMedium />
          <s.Container flex={1} jc={'center'} style={{ padding: 24 }}>
            {Number(data.totalSupply) == 2500 ? (
              <>
                <s.TextTitle>The sale has ended.</s.TextTitle>
                <s.SpacerSmall />
                <s.TextDescription>
                  You can still buy a Meme Stick on{' '}
                  <a
                    target={'_blank'}
                    href={'https://opensea.io/collection/meme-sticks'}
                  >
                    Opensea.io
                  </a>
                </s.TextDescription>
              </>
            ) : (
              <>
                <s.TextTitle>
                  2500 uniques sticks bringing meme joy to the NFT world.
                </s.TextTitle>
                <s.SpacerSmall />
                <s.TextSubTitle>
                  Price is <span className='underlined'>0.01 ETH</span>
                </s.TextSubTitle>
                <s.SpacerSmall />
                <s.TextDescription>{feedback}</s.TextDescription>
                <s.SpacerMedium />
                {blockchain.account === '' ||
                blockchain.smartContract === null ? (
                  <s.Container ai={'center'} jc={'center'}>
                    <s.TextDescription light>
                      Connect to the Ethereum network
                    </s.TextDescription>
                    <s.SpacerSmall />
                    <StyledButton
                      onClick={(e) => {
                        e.preventDefault();
                        dispatch(connect());
                        getData();
                      }}
                    >
                      CONNECT WALLET
                    </StyledButton>
                    {blockchain.errorMsg !== '' ? (
                      <>
                        <s.SpacerSmall />
                        <s.TextDescription>
                          {blockchain.errorMsg}
                        </s.TextDescription>
                      </>
                    ) : null}
                  </s.Container>
                ) : (
                  <s.Container ai={'center'} jc={'center'} fd={'column'}>
                    <StyledButton
                      disabled={claimingNft ? 1 : 0}
                      onClick={(e) => {
                        e.preventDefault();
                        claimNFTs(orderAmount);
                        getData();
                      }}
                    >
                      {claimingNft ? 'BUSY...' : 'BUY A MEME STICK'}
                    </StyledButton>
                    <br />I want to buy{' '}
                    <input
                      type='range'
                      id='quantity'
                      name='quantity'
                      min='1'
                      max='5'
                      onChange={(e) => setOrderAmount(e.target.value)}
                    />
                    <label for='quantity'>{orderAmount}</label>
                  </s.Container>
                )}
              </>
            )}
          </s.Container>
        </ResponsiveWrapper>
        <s.SpacerSmall />
        <s.Container jc={'center'} ai={'center'} style={{ width: '70%' }}>
          <s.TextDescription style={{ textAlign: 'center', fontSize: 15 }}>
            Please make sure you are connected to the Ethereum network.
            <br />
            Once you make the purchase, you cannot undo this action.
          </s.TextDescription>
          <s.SpacerSmall />
