import React, { useEffect, useState, useRef } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { connect } from './redux/blockchain/blockchainActions';
import { fetchData } from './redux/data/dataActions';
import * as s from './styles/globalStyles';
import styled from 'styled-components';
import i1 from './assets/images/1.png';

export const StyledButton = styled.button`
  padding: 10px;
  border-radius: 50px;
  border: none;
  background-color: #ffffff;
  padding: 12px;
  font-weight: bold;
  color: var(--dark-grey);
  width: 150;
  cursor: pointer;
  box-shadow: 0px 6px 0px -2px rgba(250, 250, 250, 0.3);
  -webkit-box-shadow: 0px 6px 0px -2px rgba(250, 250, 250, 0.3);
  -moz-box-shadow: 0px 6px 0px -2px rgba(250, 250, 250, 0.3);
  :active {
    box-shadow: none;
    -webkit-box-shadow: none;
    -moz-box-shadow: none;
  }
  &:disabled {
    cursor: wait;
    opacity: 0.6;
  }
`;

export const ResponsiveWrapper = styled.div`
  display: flex;
  flex: 1;
  flex-direction: column;
  justify-content: stretched;
  align-items: stretched;
  width: 100%;
  @media (min-width: 767px) {
    flex-direction: row;
  }
`;

export const StyledImg = styled.img`
  width: 300px;
  height: 300px;
  @media (min-width: 767px) {
    width: 400px;
    height: 400px;
  }
  transition: width 0.25s;
  transition: height 0.25s;
`;

function App() {
  const dispatch = useDispatch();
  const blockchain = useSelector((state) => state.blockchain);
  const [feedbackType, setFeedbackType] = useState('');
  const data = useSelector((state) => state.data);
  const [orderAmount, setOrderAmount] = useState(1);
  const [feedback, setFeedback] = useState('Will you get the golden crown ?');
  const [claimingNft, setClaimingNft] = useState(false);

  let orderMessage;
  if (orderAmount == 1) {
    orderMessage = ' Meme Stick ðŸ™';
  } else if (orderAmount > 1 && orderAmount <= 3) {
    orderMessage = ' Meme Sticks ðŸ™ðŸš€';
  } else if (orderAmount > 3 && orderAmount < 8) {
    orderMessage = ' Meme Sticks ðŸ™ðŸš€ðŸ¤¯';
  } else {
    orderMessage = ' Meme Sticks ðŸ™ðŸš€ðŸ¤¯ðŸ³';
  }

  const claimNFTs = (_amount) => {
    if (_amount <= 0) {
      return;
    }
    const isOwner =
      blockchain.account.toUpperCase() == data.owner.toUpperCase();
    console.log(`${blockchain.account.toUpperCase()} is owner ? ${data.owner}`);
    console.log(isOwner);
    if (data.balance + _amount > 10) {
      setFeedbackType('error');
      setFeedback("You can't mint more than 10 you greedy mofo");
      return;
    }
    setFeedbackType('waiting');
    setFeedback(`Grabbing your Meme Stick${orderAmount > 1 ? 's' : ''}...`);
    setClaimingNft(true);
    blockchain.smartContract.methods
      .mint(_amount)
      .send({
        // gasLimit: '285000',
        // to: '0xfb9ff562753a31d63d58a34fcf649e263a4477b4',
        from: blockchain.account,
        value: blockchain.web3.utils.toWei(
          (0.01 * _amount).toString(),
          'ether'
        ),
      })
      .once('error', (err) => {
        console.log(err);
        setFeedbackType('error');
        setFeedback('Hmm, something went wrong maybe try again ?');
        setClaimingNft(false);
      })
      .then((receipt) => {
        setFeedbackType('success');
        setFeedback("You're the happy owner of a Meme Stick ! Congrats ðŸŽ‰");
        setClaimingNft(false);
        dispatch(fetchData(blockchain.account));
      });
  };

  const getData = () => {
    if (blockchain.account !== '' && blockchain.smartContract !== null) {
      dispatch(fetchData(blockchain.account));
    }
  };

  useEffect(() => {
    getData();
    console.log(data.isOwner);
  }, [blockchain.account]);

  return (
    <s.Screen style={{ backgroundColor: 'var(--white)' }}>
      <s.Container flex={1} ai={'center'} style={{ padding: 24 }}>
        <s.TextTitle
          style={{ textAlign: 'center', fontSize: 35, fontWeight: 'bold' }}
        >
          MEME STICKS CLUB
        </s.TextTitle>
        <s.SpacerMedium />
        <ResponsiveWrapper flex={1} style={{ padding: 24 }}>
          <s.Container flex={1} jc={'center'} ai={'center'}>
            <StyledImg alt={'memestick example'} src={i1} />
            <s.SpacerMedium />
            {blockchain.account && (
              <s.TextTitle
                style={{
                  textAlign: 'center',
                  fontSize: 30,
                  fontWeight: 'bold',
                }}
              >
                {data.totalSupply}/2500 minted
              </s.TextTitle>
            )}
          </s.Container>
          <s.SpacerMedium />
          <s.Container flex={1} jc={'center'} style={{ padding: 24 }}>
            {Number(data.totalSupply) == 2500 ? (
              <>
                <s.TextTitle>The sale has ended.</s.TextTitle>
                <s.SpacerSmall />
                <s.TextDescription>
                  You can still buy a Meme Stick on{' '}
                  <a
